// entry.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import io from "socket.io-client";
import './Entry.css';

const socket = io("http://localhost:9000");

function Entry() {
  const navigate = useNavigate();
  const [userName, setUserName] = useState("");

  const enterUser = () => {
    if (userName.trim()) {
      // Emit the username to the backend
      socket.emit("userName", userName);
      
      // Navigate to the table page
      navigate('/table');
    }
  };

  return (
    <div className="body_block">
      <div className="left-image">
        <img src="/images/left_image.png" alt="Left Image" className="image" />
      </div>
      <div className="right-image">
        <img src="/images/right_image.png" alt="Right Image" className="image" />
      </div>

      <div className='player_profile_block'>
        <div className="avatar-selector">
          <button className="avatar-button">
            <img src="/images/avatar.png" alt="Player Avatar" className="avatar-image" />
          </button>
        </div>

        <div className='player_name'>
          <input
            type='text'
            id='player_name'
            placeholder='Enter your name'
            value={userName}
            onChange={(e) => setUserName(e.target.value)}
          />
        </div>
        <button className="enter_button_class" onClick={enterUser}>
          <img src="/images/enter_button.png" alt="Enter Button" className="enter_button" />
        </button>
      </div>
    </div>
  );
}

export default Entry;
