import React from "react";
import './Table_Setting.css'
import { useNavigate } from "react-router-dom";
import { useState } from "react";
import { getSocket } from './socket';

function Table_Setting(){
    const [bootAmount,setBootAmount]=useState(null);
    const [potLimit,setPotLimit]=useState(null);
    const [maxBetValue,setMaxBetValue]=useState(null);
    const navigate=useNavigate();
    const goToTable=()=>{
    const value={bootAmount,potLimit,maxBetValue};
    Socket.emit("tableSetting",value);
    navigate('/table');
}
    const handleBoot=(e)=>{
        const value = e.target.value;
        if (/^-?\d*$/.test(value)) { 
            setBootAmount(value); 
        }
    }
    const handlePot=(e)=>{
        const value = e.target.value;
        if (/^-?\d*$/.test(value)) { 
            setPotLimit(value); 
        }
    }
    const handleMax=(e)=>{
        const value = e.target.value;
        if (/^-?\d*$/.test(value)) { 
            setMaxBetValue(value); 
        }
    }
    return(
        <div className="body_block">
            <div className="setting_block">
                <text>Create Table</text>
            <div className="boot_amount">
                Boot Amount
                <input type="number" id="boot_amount" placeholder="Enter boot amount" onChange={handleBoot} />
            </div>
            <div className="pot_limit">
                Pot Limit
                <input type="number" id="pot_limit" placeholder="Enter pot limit" onChange={handlePot}/>
            </div>
            <div className="max_bet_value">
                Max Bet Value
                <input type="number" id="max_bet_value" placeholder="Enter Max Bet Value" onChange={handleMax}/>
            </div>
            <button className="create_button" id="margin_cb" onClick={goToTable}>Create Table</button>
            </div>
        </div>
    );
}

export default Table_Setting;
