import React, { useEffect, useState } from "react";
import './Table.css'
import { getSocket } from './socket';



function Table(){
    const [players,setPlayers]=useState([]);
    const [poolMoney,setPoolMoney]=useState(0);
    const [userName,setUserName]=useState("");
    const socket =getSocket();
    const [isButtonClicked, setIsButtonClicked] = useState(false);
    const [isStartClicked,setIsStartClicked]=useState(false);
    const [isDealClicked,setIsDealClicked]=useState(false);
    const [showInput,setShowInput]=useState(false);
    const [inputValue,setInputValue]=useState("");
    const [activePlayerId, setActivePlayerId] = useState(null); 
    const [isGiftClicked,setIsGiftClicked]=useState(false);
    const [isGiftDoneClicked,setIsGiftDoneClicked]=useState(true);
    
    let winners=[];
    const numberOfPlayers=players.length;

    const handleStart=()=>{
        socket.emit("handleStart");
        setIsStartClicked(true);
        
    }

    const handleChaal=()=>{
        if(isStartClicked)
        socket.emit("handleChaal");
       
    }

    const handle2xChaal=()=>{
        if(isStartClicked)
        socket.emit("handle2xChaal");
        
    }

    const handleButtonClick = () => {
            if(isStartClicked)
            setIsButtonClicked(true); // Update state to render a div
        };

    const handleBlind=()=>{
        if(isStartClicked)
        socket.emit("handleBlind");
    }

    const handle2xBlind=()=>{
        if(isStartClicked)
        socket.emit("handle2xBlind");
    }

    const handleDeal=()=>{
        if(isStartClicked)
        setIsDealClicked(true);
    }

    const handleDone=()=>{
        socket.emit("handleDone",winners);
        setIsButtonClicked(false);
        setIsDealClicked(false);
        setIsStartClicked(false);

    }

    const handleGift = (playerId) => {
        setActivePlayerId(playerId);
        setIsGiftClicked(true);
        setIsGiftDoneClicked(false);
      };
    
      const handleInputChange = (e) => {
        // Update the input value and ensure it's an integer
        const value = e.target.value;

        if (/^\d*$/.test(value)) { // Allow only numeric values
            setInputValue(value);
          }
        
      };

      const handleGiftDone=(e,playerId)=>{
        const playerGift={money:e,id:playerId}
        const money = parseInt(inputValue, 10); // Parse input as an integer
        if (!isNaN(money)) {
          const playerGift = { money, id: playerId };
          socket.emit("playerGift", playerGift);
          setActivePlayerId(null); // Reset the active player
          setInputValue(""); // Clear the input
          setIsGiftDoneClicked(true);
          setIsGiftClicked(false);
          console.log(isGiftClicked,isG)
        } else {
          alert("Please enter a valid number");
        }
      }



    const toggleSelection = (id) => {
        winners.push(id);
      };

    useEffect(()=>{

        socket.on("playerHistory",(playerHistory)=>{
            setPlayers(playerHistory);
        })
        socket.on("poolMoney",(pool_money)=>{
            setPoolMoney(pool_money);
        })
        return () => {
            socket.off("playerHistory");
            socket.off("poolMoney");
        };
    },[socket]);

    
    
    return(
        <div className="table_block">
            <div className="table_container">
                <img src="/images/table_image.png" alt="Table Image" class="table_image" />
                {isDealClicked && <p className="condDiv">Select Winners</p>}
                <div className="players_list">
                {isDealClicked && (
                    <ul>
                    {players.map((player) => (
                        <li key={player.id}>
                        <label>
                            <p> {player.userName}: {player.money}</p>
                            {/* The checkbox after the player name */}
                            <input
                            type="checkbox"
                            id="checkbox"
                            checked={player.selected}
                            onChange={() => toggleSelection(player.id)}
                            />
                        </label>
                        </li>
                    ))}
                    </ul>
                )}

                {!isDealClicked && !isStartClicked&&(
                    <ul>
                    {players.map((player) => (
                        <li key={player.id}>
                        <label>
                            <p> {player.userName}: {player.money}</p>
                         
                            {(activePlayerId !== player.id ||isGiftDoneClicked)&& (
                                <button
                                className="gift"
                                onClick={() => handleGift(player.id)}
                                >
                                Gift
                                </button>
                            )}
                        {activePlayerId === player.id &&isGiftClicked&& (
                        <div className="giftInputBlock">
                        <input
                            className="giftInput"
                            type="text"
                            placeholder="Enter an integer"
                            value={inputValue}
                            onChange={handleInputChange}
                        />
                        <button
                            className="giftDone"
                            onClick={(e) => handleGiftDone(e, player.id)}
                        >
                           Gift
                        </button>
                        </div>
                    )}
                        
                        </label>
                        </li>
                    ))}
                    </ul>
                )}
                {isStartClicked&&(
                    <ul>
                    {players.map((player) => (
                        <li key={player.id}>
                        <label>
                            <p> {player.userName}: {player.money}</p>  
                        </label>
                        </li>
                    ))}
                    </ul>
                )

                }
                     {!isStartClicked && 
                            <button className="start" onClick={handleStart}>
                            Start
                            </button>
                        }
                    {isStartClicked&&!isDealClicked&&
                            <button className="start" onClick={handleDeal}>
                                Deal
                            </button>
                        }
                    {isDealClicked && 
                            <button className="start" onClick={handleDone}>
                            Done
                            </button>
                        }
                </div>
                <div className="pool_money">{poolMoney}</div>
            </div>
             <div className="container">
                <img src="/images/moves_image.png" className="movesbg"/>
                <div className="moves_block">
                    <div className="first_section">
                    {isButtonClicked ? (
                            <button className="first" onClick={handleChaal} >
                            Chaal
                            </button>
                        ) : (
                            <button className="first" onClick={handleBlind} >
                            Blind
                            </button>
                        )}
                    <button className="second">Pack</button>
                    </div>

                    <div className="second_section">
                    <text className="score">750</text>

                        {isButtonClicked ? (
                            <p className="disabledSeeButton">Seen</p>
                        ) : (
                            <button className="seeButton" id="seeButton" onClick={handleButtonClick}>
                            See
                            </button>
                        )}
                   
                    
                    </div>

                    <div className="third_section">
                    {isButtonClicked ? (
                            <button className="third" onClick={handle2xChaal} >
                            2x Chaal
                            </button>
                        ) : (
                            <button className="third" onClick={handle2xBlind} >
                            2x Blind
                            </button>
                        )}
                    <button className="fourth" onClick={handle2xChaal}>SideShow</button>
                </div>
                </div>
                    
             </div>
        </div>
    );
}
export default Table;
