// table.js
import React, { useEffect, useState } from "react";
import './Table.css';
import io from "socket.io-client";

function Table() {
  const [players, setPlayers] = useState([]);
  const [poolMoney, setPoolMoney] = useState(0);
  const [username, setUsername] = useState(""); // Store username here
  const numberOfPlayers = players.length;

  const handleStart = () => {
    socket.emit("handleStart"); // Emit handleStart event
    window.location.reload(); // Reload page to reflect updated data
  };

  const handleChaal = () => {
    socket.emit("handleChaal", username); // Emit handleChaal with the current username
  };

  useEffect(() => {
    const newSocket = io("http://localhost:9000");

    // Listen for playerHistory event from the backend
    newSocket.on("playerHistory", (playerHistory) => {
      setPlayers(playerHistory);
      
      // Optionally, you could set the username here if the username is part of the player object
      
      if (player) {
        setUsername(player.userName); // Set username after player connects
      }
    });

    // Listen for poolMoney event from the backend
    newSocket.on("poolMoney", (pool_money) => {
      setPoolMoney(pool_money);
    });

    return () => {
      newSocket.disconnect(); // Clean up the connection on component unmount
    };
  }, []);

  return (
    <div className="table_block">
      <div className="table_container">
        <img src="/images/table_image.png" alt="Table Image" className="table_image" />
        <div className="players_list">
          {players.map((player, index) => (
            <p key={index}>{player.userName}: {player.money}</p>
          ))}
          <button className="start" onClick={handleStart}>Start</button>
        </div>
        <div className="pool_money">{poolMoney}</div>
      </div>
      <div className="container">
        <img src="/images/moves_image.png" className="movesbg" />
        <div className="moves_block">
          <div className="first_section">
            <button className="first" onClick={handleChaal}>Chaal</button>
            <button className="second">Second</button>
          </div>

          <div className="second_section">
            <text className="score">750</text>
            <text className="status">Seen</text>
            <text>{username}'s Turn</text>
          </div>

          <div className="third_section">
            <button className="third">2x Chaal</button>
            <button className="fourth">Fourth</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Table;
