import React, { useEffect, useState } from "react";
import './Table.css';
import io from "socket.io-client";

// Create socket connection once and share it across renders
const socket = io("http://localhost:9000");

function Table() {
  const [players, setPlayers] = useState([]);
  const [poolMoney, setPoolMoney] = useState(0);
  const numberOfPlayers = players.length;

  // Handle start button click
  const handleStart = () => {
    socket.emit("handleStart");  // Notify the backend to perform the 'start' action
    // Instead of window.location.reload(), handle state updates dynamically
  };

  // Handle other actions (like 'Chaal') here
  const handleChaal = () => {
    socket.emit("handleChaal"); // Emit event to backend for player action
  };

  useEffect(() => {
    // Listen for events emitted by the server (e.g., updated player history, pool money)
    socket.on("playerHistory", (playerHistory) => {
      setPlayers(playerHistory);  // Update player data
    });

    socket.on("poolMoney", (poolMoney) => {
      setPoolMoney(poolMoney);    // Update pool money
    });

    return () => {
      socket.off("playerHistory");
      socket.off("poolMoney");
    };
  }, []);

  return (
    <div className="table_block">
      <div className="table_container">
        <img src="/images/table_image.png" alt="Table Image" className="table_image" />
        <div className="players_list">
          {players.map((player, index) => (
            <p key={index}>{player.userName}: {player.money}</p>
          ))}
          <button className="start" onClick={handleStart}>Start</button>
        </div>
        <div className="pool_money">{poolMoney}</div>
      </div>
      <div className="container">
        <img src="/images/moves_image.png" className="movesbg" />
        <div className="moves_block">
          <div className="first_section">
            <button className="first" onClick={handleChaal}>Chaal</button>
            <button className="second">Second</button>
          </div>
          <div className="second_section">
            <text className="score">750</text>
            <text className="status">Seen</text>
            <text>Ojjas' Turn</text>
          </div>
          <div className="third_section">
            <button className="third">2x Chaal</button>
            <button className="fourth">Fourth</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Table;
